const sections=['#landing','#about','#portfolio','#contact'];const landingPage=()=>{const nav=$("#navigation");nav.slideUp(0);navDown=()=>nav.css('max-height','100vh').slideDown(1000);navUp=()=>nav.slideUp(700);for(let i=0;i<sections.length;i++){$(sections[i]).slideUp(0)}
$('#landing').slideDown(700)};function navigue(target){$.navGo=function(target){console.log(`${target} (target) successfull acquired.`);const hidePages=()=>{for(let i=0;i<sections.length;i++){$(sections[i]).slideUp(300)}};hidePages();console.log('Loop to hide things has executed');$('#navigation').slideUp(500);$(target).delay(300).slideDown(700);console.log('Successful! Operation navigue(target) complete.')};$.navGo(target)}